template <typename T> void read(T &x) {
	x = 0; int f = 1;
	char c = getchar();
	for (; !isdigit(c); c = getchar()) if (c == '-') f = -f;
	for (; isdigit(c); c = getchar()) x = x * 10 + c - '0';
	x *= f;
}
struct Trie {
	struct Node {
		int child[3], ind;
		bool tag;
	} a[MAXP];
	int n, root, size, ans[MAXP];
	void build(int depth, int &root, int sum, int cur) {
		root = ++size;
		if (depth == 0) {
			a[root].ind = sum;
			return;
		}
		build(depth - 1, a[root].child[0], sum, cur * 3);
		build(depth - 1, a[root].child[1], sum + cur, cur * 3);
		build(depth - 1, a[root].child[2], sum + cur + cur, cur * 3);
	}
	void init(int x) {
		build(n = x, root, 0, 1);
	}
	void shift() {
		swap(a[root].child[1], a[root].child[2]);
		a[root].tag ^= true;
	}
	void pushdown(int root) {
		if (a[root].tag) {
			for (int i = 0; i <= 2; i++) {
				swap(a[a[root].child[i]].child[1], a[a[root].child[i]].child[2]);
				a[a[root].child[i]].tag ^= true;
			}
			a[root].tag = false;
		}
	}
	void inc(int depth, int root) {
		int x = a[root].child[0];
		int y = a[root].child[1];
		int z = a[root].child[2];
		a[root].child[0] = z;
		a[root].child[1] = x;
		a[root].child[2] = y;
		if (depth == 0) return;
		pushdown(root), inc(depth - 1, a[root].child[0]);
	}
	void work(int depth, int root, int sum, int cur) {
		if (depth == 0) {
			ans[a[root].ind] = sum;
			return;
		}
		pushdown(root);
		work(depth - 1, a[root].child[0], sum, cur * 3);
		work(depth - 1, a[root].child[1], sum + cur, cur * 3);
		work(depth - 1, a[root].child[2], sum + cur + cur, cur * 3);
	}
	void getans() {
		work(n, root, 0, 1); int bit = 1;
		for (int i = 1; i <= n; i++)
			bit = bit * 3;
		for (int i = 0; i < bit; i++)
			printf("%d ", ans[i]);
		printf("\n");
	}
} Trie;
int n, len; char s[MAXN];
int main() {
	read(n), Trie.init(n);
	scanf("\n%s", s + 1);
	len = strlen(s + 1);
	for (int i = 1; i <= len; i++)
		if (s[i] == 'S') Trie.shift();
		else Trie.inc(n, Trie.root);
	Trie.getans();
	return 0;
}

