#include<iostream>
#include<algorithm>
#include<string>
#include<vector>

int main() {
  std::cin.tie(0); std::ios::sync_with_stdio(false);
  int N; std::string T; std::cin >> N >> T;
  int L = 1; for(int i = 0; i < N; ++i) L *= 3;
  std::vector<int> W(T.size(), 0), WU(T.size()+1), D(L), P(L, 0);

  int l = 1;
  for(int n = 0; n < N; ++n) {
    std::fill_n(D.begin(), l, 0);
    std::fill_n(D.begin()+l, l, l);
    std::fill_n(D.begin()+l+l, l, l+l);
    int one = l, two = l+l, three = l*3;
    bool sOdd = false;
    WU[0] = two;
    for(int k = 0; k < T.size(); ++k) {
      if(T[k] == 'S') {
        sOdd ^= true;
        std::swap(one, two);
      } else {
        if(sOdd) {
          (D[W[k]    ] += l+l) %= (l*3);
          (D[W[k]+l  ] += l+l) %= (l*3);
          (D[W[k]+l+l] += l+l) %= (l*3);
        } else {
          (D[W[k]    ] += l) %= (l*3);
          (D[W[k]+l  ] += l) %= (l*3);
          (D[W[k]+l+l] += l) %= (l*3);
        }
      }
      // update W
      if(D[W[k+1]] == two) WU[k+1] = 0;
      else if(D[W[k+1]+l] == two) WU[k+1] = l;
      else WU[k+1] = l+l;
    }
    for(int i = 0; i < W.size(); ++i) W[i] += WU[i];
    // update P
    for(int i = l*3-1; i >= 0; --i) {
      P[i] = P[i%l] + (sOdd?(three-D[i])%three: D[i]);
    }
    l *= 3;
  }
  for(int i = 0; i < L-1; ++i) std::cout << P[i] << " ";
  std::cout << P[L-1] << std::endl;
  return 0;
}

