struct Node {
	int val;
	int go0, go1, go2;
};
vector<Node> V;
int newNode() {
	V.emplace_back();
	return len(V) - 1;
}
void build(int root, int n, int cur, int inc, int depth) {
	if(depth == n) {
		V[root].go0 = V[root].go1 = V[root].go2 = NIL;
		V[root].val = cur;
	} else {
		int A = newNode();
		int B = newNode();
		int C = newNode();
		V[root].go0 = A;
		V[root].go1 = B;
		V[root].go2 = C;
		build(A, n, cur, 3 * inc, depth + 1);
		build(B, n, cur + inc, 3 * inc, depth + 1);
		build(C, n, cur + 2 * inc, 3 * inc, depth + 1);
	}
}
void shift1(int root) {
	if(V[root].go0 == NIL || V[root].go1 == NIL || V[root].go2 == NIL) {
		return;
	}
	int d0 = V[root].go0;
	int d1 = V[root].go1;
	int d2 = V[root].go2;
	V[root].go1 = d0;
	V[root].go2 = d1;
	V[root].go0 = d2;
	shift1(d2);
}
void shift2(int root) {
	if(V[root].go0 == NIL || V[root].go1 == NIL || V[root].go2 == NIL) {
		return;
	}
	int d0 = V[root].go0;
	int d1 = V[root].go1;
	int d2 = V[root].go2;
	V[root].go2 = d0;
	V[root].go0 = d1;
	V[root].go1 = d2;
	shift2(d1);
}
int answ[1000 * 1000];
void show1(int root, int cur, int inc) {
	if(V[root].go0 == NIL || V[root].go1 == NIL || V[root].go2 == NIL) {
		answ[V[root].val] = cur;	
	} else {
		show1(V[root].go0, cur, 3 * inc);
		show1(V[root].go1, cur + inc, 3 * inc);
		show1(V[root].go2, cur + 2 * inc, 3 * inc);
	}
}
void show2(int root, int cur, int inc) {
	if(V[root].go0 == NIL || V[root].go1 == NIL || V[root].go2 == NIL) {
		answ[V[root].val] = cur;	
	} else {
		show2(V[root].go0, cur, 3 * inc);
		show2(V[root].go2, cur + inc, 3 * inc);
		show2(V[root].go1, cur + 2 * inc, 3 * inc);
	}
}
void solve() {
	int n = fetch<int>();
	int root = newNode();
	build(root, n, 0, 1, 0);
	auto cmd = fetch<string>();
	int flag = 1;
	for(auto& ch: cmd) {
		if(ch == 'S') {
			flag ^= 1;
		} else if(ch == 'R') {
			if(flag) {
				shift1(root);
			} else {
				shift2(root);
			}
		} else {
			assert(false);
		}
	}
	if(flag) {
		show1(root, 0, 1);
	} else {
		show2(root, 0, 1);
	}
	int mod = 1;
	for(int i = 0; i < n; ++i) {
		mod *= 3;
	}
	for(int i = 0; i < mod; ++i) {
		cout << answ[i] << ' ';
	}
	cout << '\n';
}

int main() {
	fast_io();
	solve();

	return 0;
}

