int ch[800000][3],ans[600000],cnt;
void build(int k,int dep)
{
	if(!dep)return;
	ch[k][0]=k*3-1,ch[k][1]=k*3,ch[k][2]=k*3+1;
	build(k*3-1,dep-1),build(k*3,dep-1),build(k*3+1,dep-1);
}
void pushtag(int k)
{
	swap(ch[k][1],ch[k][2]);
	tag[k]^=1;
}
void work(int k,int dep)
{
	if(!dep)return;
	if(tag[k])
	{
		pushtag(ch[k][0]);
		pushtag(ch[k][1]);
		pushtag(ch[k][2]);
		tag[k]=0;
	}
	int a=ch[k][0],b=ch[k][1],c=ch[k][2];
	ch[k][0]=c,ch[k][1]=a,ch[k][2]=b;
	work(ch[k][0],dep-1);
}
void dfs(int k,int dep,int cur)
{
	if(!dep)
	{
		int s=0;k-=p+1;
		for(int i=1;i<=n;++i)s=s*3+k%3,k/=3;
		ans[s]=cur;
		return;
	}
	if(tag[k])
	{
		pushtag(ch[k][0]);
		pushtag(ch[k][1]);
		pushtag(ch[k][2]);
		tag[k]=0;
	}
	dfs(ch[k][0],dep-1,cur);
	dfs(ch[k][1],dep-1,cur+po[n-dep]);
	dfs(ch[k][2],dep-1,cur+2*po[n-dep]);
}
int main()
{
	po[0]=1;
	for(int i=1;i<=12;++i)po[i]=3*po[i-1];
