	for(int i=1;i<=m;++i)
	{
		if(a[i]=='S')pushtag(1);
		else work(1,n);
	}
	p=(po[n]-1)/2;
	dfs(1,n,0);
	for(int i=0;i<po[n];++i)printf("%d ",ans[i]);
	return 0;
}

