void dfs(int id,int p,int q,string s){
    if(id==nn){
        res[p] = q;
        return;
    }
    int n = s.size();
    string t;
    for(int i=0;i<n;i++){
        if(s[i]=='S'){
            if(t.size()!=0&&t.back()=='S'){
                t.pop_back();
            }else{
                t.push_back(s[i]);
            }
        }else{
            t.push_back(s[i]);
        }
    }
    for(int pp=0;pp<3;pp++){
        string nxt;
        int k = pp;
        for(int i=0;i<n;i++){
            if(t[i]=='S'){
                if(k==1||k==2){
                    k = 3-k;
                }
                nxt.push_back('S');
            }else if(t[i]=='R'){
                k++;
                if(k==3){
                    nxt.push_back('R');
                    k = 0;
                }
            }
        }
        dfs(id+1,p+P[id]*pp,q+P[id]*k,nxt);
    }
}
int main(){
    string s;
    cin >> nn >> s;
    P[0] = 1;
    for(int i=1;i<=12;i++){
        P[i] = P[i-1]*3;
    }
    dfs(0,0,0,s);
    int k = 1;
    for(int i=0;i<nn;i++){
        k*=3;
    }
    rep(i,k){
        cout << res[i] << " ";
    }
    cout << endl;
    return 0;
}

