#include <bits/stdc++.h>
using namespace std;

int po3(int e){
    int ret = 1;
    for(int i = 0; i<e; i++){
        ret *= 3;
    }

    return ret;
}

int conjug(int x){
    int mult = 1;
    int ret = 0;

    while(x){
        int t = x % 3;
        if(t == 1) ret += mult * 2;
        if(t == 2) ret += mult;

        x /= 3;
        mult *= 3;
    }

    return ret;
}

struct EndingSet{
