using namespace std;

long long pow(long long a, long long n) {
    long long res = 1;
    while (n > 0) {
        if (n & 1) res = res * a;
        a = a * a;
        n >>= 1;
    }
    return res;
}

long long conv(long long x) {
  vector<int> s;
  while (x != 0) {
    int y = x % 3;
    s.push_back(y);
    x /= 3;
  }

  int n = s.size();
  long long res = 0;
  for (int i = n - 1; i >= 0; --i) {
    if (s[i] == 2) {
      res += pow(3, i);
    } else if (s[i] == 1) {
      res += 2LL * pow(3, i);
    }
  }
  return res;
}

void printV(vector<long long>& p, long long MOD) {
  for (long long i = 0; i < MOD; ++i) {
    if (i != 0) {
      cout << " ";
    }
    cout << p[i];
  }
  cout << endl;
}

