  cin >> t;

  long long MOD = pow(3, n);
  vector<long long> p(MOD);
  for (long long i = 0; i < MOD; ++i) {
    p[i] = i;
  }
  vector<long long> tmp(MOD);
  long long tS = t.size();
  for (long long i = 0; i < tS; ++i) {
    if (t[i] == 'S') {
      for (long long j = 0; j < MOD; ++j) {
        long long x = conv(p[j]);
        tmp[j] = x;
      }
    } else {
      for (long long j = 0; j < MOD; ++j) {
        tmp[j] = p[(j - 1 + MOD) % MOD];
      }
    }
    p = tmp;
  }

  printV(p, MOD);
}

