	build(0,0);
	rep(i,s.size())
	{
		if(s[i]=='R') Rot(1);
		else Swp();
	}
	getans(1,0,0);
	rep(i,pow(3,n)) cout<<ans[i]<<' ';
	cout<<endl;
	return 0;
}

#include <bits/stdc++.h>
using namespace std;
pair<vector<int>, vector<int> > solve(int n, const string &T) {
	if (!n) {
		return make_pair(vector<int>(1, 0), vector<int>(T.size(), 0));
	}
	vector<int> P, W;
	tie(P, W) = solve(n - 1, T);
	int sz = P.size();
	vector<int> nP(3 * sz), nW(T.size()), D(3 * sz), lt(3 * sz, 0);
	for (int i = 0; i < 3; i++) for (int j = 0; j < sz; j++) D[i * sz + j] = i;
	int ct = 0;
	function<void(int)> upd = [&](int i) {
		if (D[i] && (ct - lt[i]) % 2 == 1) D[i] = 3 - D[i];
		lt[i] = ct;
	};
	for (int i = 0; i < (int)T.size(); i++) {
		for (int j = W[i]; j < 3 * sz; j += sz) {
			upd(j);
			if (D[j] == 2) nW[i] = j;
		}
		if (T[i] == 'S') ct++;
		else {
			for (int j = W[i]; j < 3 * sz; j += sz) {
				D[j] = (D[j] + 1) % 3;
			}
		}
	}
	for (int i = 0; i < 3 * sz; i++) {
		upd(i);
		nP[i] = P[i % sz] + D[i] * sz;
	}
	return make_pair(nP, nW);
}
int main() {
	int n;
	string T;
	cin >> n >> T;
	vector<int> ans = solve(n, T).first;
	for (int x : ans) cout << x << ' ';
	return 0;
}

