inline void read(T &x) {
	x = 0;
	char ch = getchar(), w = 0;
	while (!isdigit(ch)) w = (ch == '-'), ch = getchar();
	while (isdigit(ch)) x = (x << 1) + (x << 3) + (ch ^ 48), ch = getchar();
	x = w ? -x : x;
	return;
}

void build(int &k, int dep, int x) {
	k = ++tot;
	if (dep == n) {
		ind[k] = x;
		return;
	}
	for (R int i = 0; i <= 2; ++i)
		build(ch[k][i], dep + 1, x + i * pw[dep]);
	return;
}

void modify(int k, int dep) {
	if (dep == n) return;
	modify(ch[k][to[2]], dep + 1);
	int tmp = ch[k][to[0]];
	ch[k][to[0]] = ch[k][to[2]], ch[k][to[2]] = ch[k][to[1]], ch[k][to[1]] = tmp;
	return;
}

void dfs(int k, int dep, int x) {
	if (dep == n) {
		ans[ind[k]] = x;
		return;
	}
	for (R int i = 0; i <= 2; ++i)
		dfs(ch[k][to[i]], dep + 1, x + i * pw[dep]);
	return;
}

