	build(root, 0, 0);
	for (R int i = 1, m = strlen(s + 1); i <= m; ++i) {
		if (s[i] == 'S')
			swap(to[1], to[2]);
		else
			modify(root, 0);
	}
	dfs(root, 0, 0);
	for (R int i = 0; i < pw[n]; ++i)
		cout << ans[i] << ' ';
