void salsa(vector<int> &ans){
  for(int i=0;i<(int)ans.size();i++){
    int a = ans.at(i);
    int j=0;
    while(a > 3){
      if(a%3 == 1){
        ans.at(i) += pow(3,j);
      }
      else if(a%3 == 2){
        ans.at(i) -= pow(3,j);
      }
      a = a/3;
      j++;
    }
    if(a == 1){
      ans.at(i) += pow(3,j);
    }
    else if(a == 2){
      ans.at(i)-= pow(3,j);
    }
    if(a == 3){
      ans.at(i) += pow(3,j+1);
    }
  }
}
void rumba(vector<int> &ans){
  for(int j=0;j<(int)ans.size();j++){
    if(ans.at(j)+1 < (int)ans.size()){
      ans.at(j) ++;
    }
    else{
      ans.at(j) = ans.at(j)+1 - (int)ans.size();
    }
  }
}

