      [&](auto self, int n, string& s, int base, int val, int pos) -> void {
        if (n == 0) {
          perm[val] = pos;
          return;
        }
        for (int d = 0; d < 3; ++d) {
          string s2;
          int d2 = d;
          for (char c : s) {
            if (c == 'S') {
              if (!s2.empty() && s2.back() == 'S') {
                s2.pop_back();
              } else {
                s2 += 'S';
              }
              if (d2 != 0) {
                d2 = 3 - d2;
              }
            } else {
              if (d2 == 2) {
                s2 += 'R';
                d2 = 0;
              } else {
                ++d2;
              }
            }
          }
          self(n - 1, s2, base * 3, val + base * d, pos + base * d2);
        }
      });
  int n;
  string s;
  cin >> n >> s;
  perm.resize((int)pow(3, n));
  solve(n, s, 1, 0, 0);
  for (int p : perm) {
    cout << p << ' ';
  }
  cout << '\n';
  return 0;
}

