        int M = pow(3, n);
        vector<int> D(M);
        for(int i=0; i<M; i++) D[i] = i/(M/3);
        W[n].resize(L);
        vector<int> done(M);
        int s = 0;
        for(int j=0; j<L; j++){
            int w = W[n-1][j];
            for(int i=w; i<M; i+=M/3){
                if((s-done[i]) % 2 && D[i]) D[i] = 3-D[i];
                done[i] = s;
