        for(int i=0; i<M; i++) if((s-done[i]) % 2 && D[i]) D[i] = 3-D[i];
        P[n].resize(M);
        for(int i=0; i<M; i++) P[n][i] = D[i]*(M/3) + P[n-1][i%(M/3)];
