struct tree {
	static const int maxn = 531441;
	int go[maxn][3];
	int lz[maxn], id[maxn];
	int tot;
	void push(int x) {
		swap(go[x][1], go[x][2]);
		lz[x] ^= 1;
	}
	void pd(int x) {
		if (lz[x]) {
			push(go[x][0]);
			push(go[x][1]);
			push(go[x][2]);
			lz[x] = 0;
		}
	}
	void build(int &x, int lvl, int num) {
		x = ++ tot;
		if (K == lvl) {
			id[x] = num;
			return ;
		}
		build(go[x][0], lvl + 1, num);
		build(go[x][1], lvl + 1, num + pw[lvl]);
		build(go[x][2], lvl + 1, num + 2 * pw[lvl]);
	}
	void add(int x, int lvl) {
		if (K == lvl) return ;
		pd(x);
		add(go[x][2], lvl + 1);
		swap(go[x][1], go[x][2]);
		swap(go[x][0], go[x][1]);
	}
	void prt(int x, int lvl, int num) {
		if (K == lvl) {
			ans[id[x]] = num;
			return ;
		}
		pd(x);
		prt(go[x][0], lvl + 1, num);
		prt(go[x][1], lvl + 1, num + pw[lvl]);
		prt(go[x][2], lvl + 1, num + 2 * pw[lvl]);
	}
} tr;

