int get(int x) {
    vector<int> bts; 
    for (int i = 0; i < L; ++i) {
        bts.pb(x % 3); 
        x /= 3; 
    }
    int left = 0;
    int right = 0; 
    for (int i = 6; i >= 0; --i) {
        left *= 3;
        left += bts[i]; 
    }
    for (int i = L - 1; i >= 7; --i) {
        right *= 3; 
        right += bts[i]; 
    }
    int id = 0; 
    if (left && dp1[left] == -1) {
        return merge(res[left], right);  
    }
    if (left) {    
        id = dp1[left]; 
        change(right, 0, id - 1);             
        left = 0; 
    }                     
    while (next[id] != -1) {
        change(right, id, next[id] - 1);
        id = next[id];                                                                                                                                         
    }
    left = to[id]; 
    return merge(left, right); 
}


