mt19937 rng(chrono::steady_clock::now().time_since_epoch().count());
typedef long long LL;

int getRandom() {
    return uniform_int_distribution<int>(INT_MIN, INT_MAX)(rng);
}

typedef struct item * pitem;
struct item {
    int prior, value, cnt;
    bool rev;

    item(int value):prior(getRandom()), value(value) {
        cnt = 0;
        rev = 0;
        l = r = nullptr;
    }

    pitem l, r;
};

map< int , int >p3;

namespace Treap {
    int cnt (pitem it) {
        return it != nullptr? it->cnt : 0;
    }

    void upd_cnt (pitem it) {
        if (it!=nullptr) {
            it->cnt = cnt(it->l) + cnt(it->r) + 1;
        }
    }

    void push(pitem it);

    void merge (pitem & t, pitem l, pitem r) {
        push (l);
        push (r);
        if (l==nullptr || r==nullptr)
            t = (l!=nullptr) ? l : r;
        else if (l->prior > r->prior)
            merge (l->r, l->r, r),  t = l;
        else
            merge (r->l, l, r->l),  t = r;
        upd_cnt (t);
    }

    void split (pitem t, pitem & l, pitem & r, int key, int add = 0) {
        if (t==nullptr) {
            l = r = nullptr;
            return;
        }
        push (t);
        int cur_key = add + cnt(t->l);

        if (key <= cur_key)
            split (t->l, l, t->l, key, add),  r = t;
        else
            split (t->r, t->r, r, key, add + 1 + cnt(t->l)),  l = t;
        upd_cnt (t);
    }

    void push (pitem it) {
        if (it != nullptr && it->rev == true) {
            auto jt = p3.find(it->cnt);
            assert(jt != p3.end());

            it->rev = false;
            if (it->cnt > 1) {
                int base = it->cnt/3;
                pitem A, B, C;
                split(it, A, B, base);
                split(B, B, C, base);
                A->rev ^= 1;
                B->rev ^= 1;
                C->rev ^= 1;
                merge(B, C, B);
                merge(it, A, B);
            }
        }
    }

    void insert (pitem & t, int key, int value) {

        pitem x = new item(value);

        pitem L, R;
        split(t, L, R, key);
        merge(L, L, x);
        merge(t, L, R);

        upd_cnt(t);
    }

    int erase (pitem & t, int key) {
        assert(cnt(t) > key);

        pitem L, MID, R;
        split(t, L, MID, key);
        split(MID, MID, R, 1);
        merge(t, L, R);
        upd_cnt(t);

        int x = MID->value;
        delete MID;

        return x;
    }

    void output (pitem t, vector< int >&v) {
        if (t==nullptr)  return;
        push (t);
        output (t->l, v);
        v.push_back(t->value);
        output (t->r, v);
    }

    void output2 (pitem t) {
        if (t==nullptr)  return;
        push (t);
//        cout << "(";
        output2 (t->l);
        cout << (t->value) << " ";
        output2 (t->r);
//        cout << ")";
    }
}

