struct B {
    B* p[3] = {};
    int idx;
    bool rev = false;
    void push() {
        for (auto c : p) if (rev && c) c->reverse();
        rev = 0;
    }
    void reverse() {
        rev ^= 1;
        swap(p[1], p[2]);
    }
};

int main() {
    // ios_base::sync_with_stdio(false);
    // cin.tie(NULL);
    // cout.tie(NULL); 
    int N;
    while (cin >> N) {
		int tot = 1;
        rep(i, N) tot *= 3;
        B root;
        rep(i, tot) {
            B* s = &root;
            int c = i;
            rep(j, N) {
                int k = c % 3; c /= 3;
                if (!s->p[k]) s->p[k] = new B();
                s = s->p[k];
            }
            s->idx = i;
        }
        string t; cin >> t;
        rep(i, t.length()) {
            if (t[i] == 'S') root.reverse();
            else {
                B* c = &root;
                rep(i, N) {
                    c->push();
                    B* tmp = c->p[2];
                    c->p[2] = c->p[1];
                    c->p[1] = c->p[0];
                    c->p[0] = tmp;
                    c = c->p[0];
                }
            }
        }
        VI pos(tot);
        rep(i, tot) {
            B* s = &root;
            int c = i;
            rep(j, N) {
                int k = c % 3; c /= 3;
                s = s->p[k];
            }
            pos[s->idx] = i;
        }
        rep(i, tot)
            cout << pos[i] << (i + 1 == tot ? '\n' : ' ');
    }
    return 0;
}

