int N;

inline void update(int n, int indx, int cntS){
    if( ((cntS&1) != (lastUpdated[n][indx]&1)) && D[n][indx]){
        // conduct salsa
        D[n][indx] = 3-D[n][indx];
    }
    // update the lastUpdated index
    lastUpdated[n][indx] = cntS;
}

int pow3[16];

int main(){
    scanf("%d", &N);
    scanf(" %s", T);

    int M = strlen(T);

    pow3[0] = 1;
    for(int i = 1; i <= N; i ++){
        pow3[i] = pow3[i-1]*3;
    }

    //memset(W, 0, sizeof(W));
    for(int n = 1; n <= N; n ++){
        //printf("ok");
        for(int j = 0; j < pow3[n-1]; j ++){
            D[n][j] = 0;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        for(int j = pow3[n-1]; j < (pow3[n-1]<<1); j ++){
            D[n][j] = 1;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        for(int j = (pow3[n-1]<<1); j < pow3[n]; j ++){
            D[n][j] = 2;
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        W[n][0] = pow3[n]-1;
    }

    //memset(lastUpdated, 0, sizeof(lastUpdated));
    int cntS2 = 0;
    for(int n = 1; n <= N; n ++){
        int cntS = 0;
        for(int i = 0; i < M; i ++){
            int indx0 = W[n-1][i];
            int indx1 = W[n-1][i]+pow3[n-1];
            int indx2 = W[n-1][i]+(pow3[n-1]<<1);

            if(indx1 >= pow3[n]){indx1 -= pow3[n];}
            if(indx2 >= pow3[n]){indx2 -= pow3[n];}

            update(n, indx0, cntS+(T[i]=='S'));
            update(n, indx1, cntS+(T[i]=='S'));
            update(n, indx2, cntS+(T[i]=='S'));
            if(D[n][indx0] == 2){
                W[n][i] = indx0;
            }else if(D[n][indx1] == 2){
                W[n][i] = indx1;
            }else{
                W[n][i] = indx2;
            }

            if(T[i] == 'R'){
                D[n][indx0] = (D[n][indx0]+1);
                D[n][indx1] = (D[n][indx1]+1);
                D[n][indx2] = (D[n][indx2]+1);
                if(D[n][indx0] == 3){D[n][indx0] = 0;}
                if(D[n][indx1] == 3){D[n][indx1] = 0;}
                if(D[n][indx2] == 3){D[n][indx2] = 0;}
                lastUpdated[n][indx0] = cntS;
                lastUpdated[n][indx1] = cntS;
                lastUpdated[n][indx2] = cntS;
            }else{
                cntS ++;
            }

            //printf("W[%d][%d]=%d\n", n, i+1, W[n][i+1]);
        }

        cntS2 = cntS;
    }

    for(int n = 1; n <= N; n ++){
        for(int i = 0; i < pow3[n]; i ++){
            update(n, i, cntS2);
        }

        int cnt = 0;
        for(int i = 0; i < pow3[N]; i ++){
            res[i] += D[n][cnt++]*pow3[n-1];
            if(cnt == pow3[n]){cnt = 0;}
        }
    }

    for(int i = 0; i < pow3[N]; i ++){
        /*int temp = 0;
        for(int n = 1; n <= N; n ++){
            temp += D[n][i%pow3[n]]*pow3[n-1];
        }*/
        //putInt(temp);
        putInt(res[i]);
    }

    return 0;
}

#include<bits/stdc++.h>
using namespace std;
using UL = unsigned int;
using ULL = unsigned long long;
using LL = long long;
#define rep(i, n) for(UL i = 0; i < (n); i++)

struct Problem {
public:

	const string C = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
	static const UL KN = 62;
	UL CN[62];
	UL L;
	UL unknowns;
	string ans;
	UL Query = 0;
	UL tg[2] = {};

	void Solve2(char c, UL n, UL r = ~0u, UL s = 0) {
		if (r == ~0u) { r = unknowns; }
		if (n == 0) return;
		if (r == n) {
			rep(i, n) {
				ans[ans.find('?', s)] = c;
				unknowns--;
			}
			return;
		}
		UL m = r >> 1;
		UL t = s; rep(i, m) t = ans.find('?', t) + 1;
		string tmp = ans;
		for (UL i = 0; i < L; i++) {
			if (tmp[i] == '?') {
				if (s <= i && i < t) tmp[i] = c;
				else tmp[i] = C[tg[0]];
			}
		}

		printf("? %s\n", tmp.c_str()); fflush(stdout);
		UL res; scanf("%d", &res);
		res = unknowns - res;
		Solve2(c, res, m, s);
		Solve2(c, n - res, r - m, t);
	}

	void Solve() {
		rep(i, KN) {
			printf("? ");
			rep(j, 128) printf("%c", C[i]);
			printf("\n"); fflush(stdout);
			UL res; scanf("%d", &res);
			CN[i] = 128 - res;
			L += 128 - res;
		}
		ans.assign(L, '?');
		unknowns = L;

		//if (CN[0] > CN[1]) { tg[0] = 0; tg[1] = 1; }
		//else { tg[0] = 1; tg[1] = 0; }
		//for (UL i = 2; i < KN; i++) {
		//	UL p = i;
		//	if (CN[p] > CN[tg[0]]) swap(p, tg[0]);
		//	if (CN[p] > CN[tg[1]]) swap(p, tg[1]);
		//}

		tg[0] = 0; tg[1] = 1;

		rep(i, L) {
			printf("? ");
			rep(j, L) printf("%c", (j == i) ? C[tg[1]] : C[tg[0]]);
			printf("\n"); fflush(stdout);
			UL res; scanf("%d", &res);
			if (res > L - CN[tg[0]]) { ans[i] = C[tg[0]]; unknowns--; }
			else if (res < L - CN[tg[0]]) { ans[i] = C[tg[1]]; unknowns--; }
		}

		for (UL i = 0; i < KN; i++) {
			if (i == tg[0]) continue;
			if (i == tg[1]) continue;
			Solve2(C[i], CN[i]);
		}

		printf("! %s\n", ans.c_str());
		fflush(stdout);
	}
};

int main() {
	unique_ptr<Problem> p(new Problem());
	p->Solve();
	return 0;
}

