using namespace std;
const int maxn=15;
const int maxpos=531445;
int n,times[maxn],jump[maxpos],ans[maxpos];
vector<pair<int,bool> > change;
void init()
{
    times[0]=1;
    for(int i=1;i<=n;i++)
        times[i]=times[i-1]*3;
    return;
}
int main()
{
    ios::sync_with_stdio(0);
    cin.tie(0); cout.tie(0);
    cin>>n;
    init();
    string ss;
    cin>>ss;
    char last=ss[0];
    int num=0;
    for(int i=0;i<(int)ss.length();i++)
    {
        if(ss[i]!=last)
        {
            change.push_back(make_pair(num,(last=='S')));
            last=ss[i]; num=1;
        }
        else num++;
    }
    change.push_back(make_pair(num,(last=='S')));
    for(int i=0;i<times[n];i++)
    {
        int cur=i,now=0;
        for(int j=n-1;j>=0;j--)
        {
            int val=cur/times[j];
            if(val==2) now+=times[j];
            if(val==1) now+=2*times[j];
            cur%=times[j];
        }
        jump[i]=now;
    }
    for(int i=0;i<times[n];i++)
    {
        int now=i;
        for(int j=0;j<(int)change.size();j++)
        {
            if(change[j].second)
            {
                if(change[j].first%2)
                    now=jump[now];
            }
            else now=(now+change[j].first)%times[n];
        }
        ans[i]=now;
    }
    for(int i=0;i<times[n];i++)
        cout<<ans[i]<<(i==times[n]-1?'\n':' ');
    return 0;
}

#include<bits/stdc++.h>
using namespace std;
int n, m;

struct Node{
	int lazy, ch[3], va;
}p[3030303];


int cnt = 1;

void ins(int x)
{
	int now = 1;
	int _x = x;
	for(int i = 0; i < n; i++)
	{
		p[now].va = -1;
		int q = x%3;
		if(!p[now].ch[q])
			p[now].ch[q] = ++cnt;
		x /= 3;
		now = p[now].ch[q];
	}
	p[now].va = _x;
}
int ans[1010101];

int _3 = 1, sum = 0;

void dfs(int now)
{
	if(p[now].lazy)
	{
		swap(p[now].ch[1], p[now].ch[2]);
		for(int j = 0; j < 3; j++)
			p[p[now].ch[j]].lazy ^= 1;
		p[now].lazy = 0;
	}
	if(p[now].va != -1)
	{
		ans[p[now].va] = sum;
		return;
	}
	for(int j = 0; j < 3; j++)
	{
		sum += _3 * j;
		_3 *= 3;
		dfs(p[now].ch[j]);
		_3 /= 3;
		sum -= _3 * j;
	}
}

