				{
					swap(p[now].ch[1], p[now].ch[2]);
					for(int j = 0; j < 3; j++)
						p[p[now].ch[j]].lazy ^= 1;
					p[now].lazy = 0;
				}
				int nxt = p[now].ch[2];
				p[now].ch[2] = p[now].ch[1];
				p[now].ch[1] = p[now].ch[0];
