const int N = 1001000;
string T;
int P[N],W[N],nP[N],nW[N],n,tag[N],D[N];
void solve(int n){
	int siz=pow(3,n-1)+0.5;
	for(int i=0;i<3;i++)
		for(int j=0;j<siz;j++)
			D[i*siz+j]=i,tag[i*siz+j]=0;
	int Tag=0;
	auto upd =[&](int x){
		if(tag[x]!=Tag&&D[x]) D[x]=3-D[x];
		tag[x]=Tag;
	};
	for(int i=0;i<T.size();i++){
		for(int j=0;j<3;j++){
			int w=j*siz+W[i];
			upd(w);
			if(D[w]==2) nW[i]=w;
		}
		if(T[i]=='S') Tag^=1;
		else{
			for(int j=0;j<3;j++){
				int w=j*siz+W[i];
				D[w]=(D[w]+1)%3;
			}
		}
	}
	for(int i=0;i<3;i++)
		for(int j=0;j<siz;j++){
			upd(i*siz+j);
			nP[i*siz+j]=D[i*siz+j]*siz+P[j];
		}
	memcpy(P,nP,sizeof(P));
	memcpy(W,nW,sizeof(W));
}
