using namespace std;
using UL = unsigned int;
using ULL = unsigned long long;
using LL = long long;
#define rep(i, n) for(UL i = 0; i < (n); i++)

struct Problem {
public:

	const string C = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
	static const UL KN = 62;
	UL CN[62];
	UL L;
	UL unknowns;
	string ans;
	UL Query = 0;
	UL tg[2] = {};

	void Solve2(char c, UL n, UL r = ~0u, UL s = 0) {
		if (r == ~0u) { r = unknowns; }
		if (n == 0) return;
		if (r == n) {
			UL t = s;
			rep(i, n) {
				t = ans.find('?', t);
				ans[t] = c;
				unknowns--;
			}
			return;
		}
		UL m = r >> 1;
		UL t = s; rep(i, m) t = ans.find('?', t) + 1;
		string tmp = ans;
		for (UL i = 0; i < L; i++) {
			if (tmp[i] == '?') {
				if (s <= i && i < t) tmp[i] = c;
				else tmp[i] = C[tg[0]];
			}
		}

		printf("? %s\n", tmp.c_str()); fflush(stdout);
		UL res; scanf("%d", &res);
		res = unknowns - res;
		Solve2(c, res, m, s);
		Solve2(c, n - res, r - m, t);
	}

	void Solve() {
		rep(i, KN) {
			printf("? ");
			rep(j, 128) printf("%c", C[i]);
			printf("\n"); fflush(stdout);
			UL res; scanf("%d", &res);
			CN[i] = 128 - res;
			L += 128 - res;
		}
		ans.assign(L, '?');
		unknowns = L;

		if (CN[0] > CN[1]) { tg[0] = 0; tg[1] = 1; }
		else { tg[0] = 1; tg[1] = 0; }
		for (UL i = 2; i < KN; i++) {
			UL p = i;
			if (CN[p] > CN[tg[0]]) swap(p, tg[0]);
			if (CN[p] > CN[tg[1]]) swap(p, tg[1]);
		}

		tg[0] = 0; tg[1] = 1;

		rep(i, L) {
			printf("? ");
			rep(j, L) printf("%c", (j == i) ? C[tg[1]] : C[tg[0]]);
			printf("\n"); fflush(stdout);
			UL res; scanf("%d", &res);
			if (res > L - CN[tg[0]]) { ans[i] = C[tg[0]]; unknowns--; }
			else if (res < L - CN[tg[0]]) { ans[i] = C[tg[1]]; unknowns--; }
		}

		for (UL i = 0; i < KN; i++) {
			if (i == tg[0]) continue;
			if (i == tg[1]) continue;
			Solve2(C[i], CN[i]);
		}

		printf("! %s\n", ans.c_str());
		fflush(stdout);
	}
};

int main() {
	unique_ptr<Problem> p(new Problem());
	p->Solve();
	return 0;
}

