int bin[15];
int n,m,ans[MAXN];
struct Trie
{
	int son[MAXN][3],tot,root;
	int val[MAXN];
	bool rev[MAXN];
	void insert(int v)
	{
		int p=root;
		for(int i=0;i<=n-1;i++)
		{
			int c=v/bin[i];c%=3;
			if(!son[p][c])son[p][c]=++tot;
			p=son[p][c];
		}val[p]=v;
	}
	void down(int p)
	{
		if(!rev[p])return ;
		swap(son[p][1],son[p][2]);
		rev[son[p][0]]^=1;rev[son[p][1]]^=1;rev[son[p][2]]^=1;rev[p]=0;
	}
	void addone(int p,int dep)
	{
		if(dep==0)return ;
		down(p);int a=son[p][0],b=son[p][1],c=son[p][2];
		son[p][0]=c;son[p][1]=a;son[p][2]=b;
		addone(son[p][0],dep-1);
	}
	void getans(int p,int dep,int nowp)
	{
		if(dep==n){ans[val[p]]=nowp;return ;}
		down(p);
		getans(son[p][0],dep+1,nowp);
		getans(son[p][1],dep+1,nowp+bin[dep]);
		getans(son[p][2],dep+1,nowp+2*bin[dep]);
	}
}tr;
