struct B {
    B* p[3] = {};
    int idx;
    bool rev = false;
    void push() {
        for (auto c : p) if (rev && c) c->reverse();
        rev = 0;
    }
    void reverse() {
        rev ^= 1;
        swap(p[1], p[2]);
    }
};

