struct Data {
  vector<int> vals;
  string s;
  void append(char c) {
    if (!s.empty() && s.back() == 'S' && c == 'S') {
      s.pop_back();
    } else {
      s += c;
    }
  }
  void swap(Data& ot) {
    vals.swap(ot.vals);
    s.swap(ot.s);
  }
};

void solve(__attribute__((unused)) bool read) {
  int n;
  cin >> n;
  string t;
  cin >> t;
  string s;
  for (char c : t) {
    if (c == 'S' && !s.empty() && s.back() == 'S') {
      s.pop_back();
      continue;
    }
    s += c;
  }
  int step = 1;
  for (int i = 0; i < n; ++i) {
    step *= 3;
  }
  vector<vector<int>> repr(step, vector<int>(n));
  for (int val = 0; val < step; ++val) {
    int m = val;
    for (int i = 0; i < n; ++i) {
      repr[val][i] = m % 3;
      m /= 3;
    }
  }
  vector<int> init(step);
  iota(all(init), 0);
  Data initial_data = {init, s};
  vector<vector<int>> res(step, vector<int>(n));
  vector<Data> cur_datas = {initial_data};
  vector<Data> next_datas;
  for (int w = 0; w < n; ++w) {
    next_datas.clear();
    for (auto& data : cur_datas) {
      vector<Data> new_datas(3);
      for (int i = 0; i < data.vals.size(); ++i) {
        new_datas[i % 3].vals.push_back(data.vals[i]);
      }
      for (char c : data.s) {
        if (c == 'R') {
          new_datas[2].append('R');
          new_datas[2].swap(new_datas[0]);
          new_datas[2].swap(new_datas[1]);
        } else {
          for (int i = 0; i < 3; ++i) {
            new_datas[i].append('S');
          }
          new_datas[1].swap(new_datas[2]);
        }
      }
      for (int i = 0; i < 3; ++i) {
        for (int x : new_datas[i].vals) {
          res[x][w] = i;
        }
        next_datas.push_back(new_datas[i]);
      }
    }
    next_datas.swap(cur_datas);
  }
  for (int i = 0; i < step; ++i) {
    int ans = 0;
    for (int j = n - 1; j >= 0; --j) {
      ans = ans * 3 + res[i][j];
    }
    cout << ans << " ";
  }
  cout << endl;

}

