int TmpTurnArr[3] = {0, 2, 1};
int turn(int p) {
    // printf("turn(%d) = ", p);
    int digit[13], cnt = 0, res = 0;
    while (p) {
        digit[cnt++] = p % 3;
        p /= 3;
    }
    while (~(--cnt)) {
        res = res * 3 + TmpTurnArr[digit[cnt]];
    }
    // printf("%d\n", res);
    return res;
}

inline M fpow(M x, int n) {
    M ret = base;
    while (n) {
        if (n & 1) ret = ret * x;
        x = x * x;
        n >>= 1;
    }
    return ret;
}

M genR(int k) {
    M ret;
    for (int i = 0; i < _3n; i++) {
        ret.p[i] = (i + k) % _3n;
    }
    return ret;
}

int chk(int p) {
    int cS = 0;
    if (t[p] != 'S') return false;
    while (t[p] == 'S') {
        cS++;
        p++;
    }
    if (!(cS & 1)) return false;
    if (t[p] == 'R') return p + 1;
    return false;
}

