      int r;
      r = x % 3;
      x /= 3;
      v.push_back(r);
    }
  return v;
}

vector<int> change12(vector<int> v) {
  for (int i = 0; i < v.size(); i++) {
    if (v.at(i) == 1)
      v.at(i) = 2;
    else if (v.at(i) == 2)
      v.at(i) = 1;
  }
  return v;
}

int from3(vector<int> v) {
  int s = 0;
  for (int i = 0; i < v.size(); i++) {
   int t = v.at(i);
    for (int j = 0; j < i; j++) {
      t *= 3;
    }
    s += t;
  }
  return s;
}


