const array<int, 3> NEXT = { 0, 2, 1 };
vector<int> POW3;
vector<vector<int>> poss;
vector<vector<string>> strs;
void init(int N, const string &T) {
    POW3.resize(N + 1);
    POW3[0] = 1;
    for (int i = 1; i <= N; i++) POW3[i] = POW3[i - 1] * 3;
    poss.resize(N + 1);
    strs.resize(N + 1);
    for (int i = 0; i <= N; i++) {
        poss[i].resize(POW3[i], -1);
        strs[i].resize(POW3[i]);
    }
    poss[0][0] = 0;
    strs[0][0] = T;
}
void f(int k, int p) {
    if (poss[k][p] != -1) return;
    f(k - 1, p % POW3[k - 1]);
    int t = p / POW3[k - 1];
    string U;
    for (auto c : strs[k - 1][p % POW3[k - 1]]) {
        if (c == 'S') {
            t = NEXT[t];
            if (!U.empty() && U.back() == 'S') {
                U.pop_back();
            } else {
                U.push_back('S');
            }
        } else {
            t = (t + 1) % 3;
            if (t == 0) {
                U.push_back('R');
            }
        }
    }
    poss[k][p] = t * POW3[k - 1] + poss[k - 1][p % POW3[k - 1]];
    strs[k][p] = U;
}
int main() {
    int N; string T; cin >> N >> T;
    init(N, T);
    for (int i = 0; i < POW3[N]; i++) {
        f(N, i);
        cout << poss[N][i] << " \n"[i + 1 == POW3[N]];
    }
    return 0;
}

