	}
	else {
		return(pow(b, e - 1) * b);
	}
}

int trt(int t, int n) {
	if (t / n == 0) return t;
	int x = (t % (n * 3)) / n;
	int y = (x == 1) ? 2 : ((x == 2) ? 1 : 0);
	return trt(t - x * n + y * n, n * 3);
}

