int calc_salsa(int u)
{
	int ret(0);
	int p(1);
	while (u > 0)
	{
		int dig = u%3;
		if (dig==1)
			dig = 2;
		else if (dig == 2)
			dig = 1;
		ret += dig * p;
		p *= 3;
		u /= 3;
	}
	return ret;
}

int fast_pow(int a, int b)
{
	int ret(1);
	for (int p(a); b; p *= p, b /= 2)
		if (b%2)
			ret *= p;
	return ret;
}

int main(void)
{
	ios_base::sync_with_stdio(false); cin.tie(0); cout.tie(0);

