		salsa[i] = calc_salsa(i);
	int pref_sz = fast_pow(3, MAGIC);
	int suff_sz = fast_pow(3, N - MAGIC);
	vector<int> perm_pref(pref_sz);
	vector<int> cur_suf(nb_tot);
	for (int i(0); i < nb_tot; ++i)
		cur_suf[i] = i/pref_sz;
	for(int i(0); i < pref_sz; ++i)
		perm_pref[i] = i;
