					{
						if (salsa_faites[i + j*pref_sz] % 2 != nb_salsa%2)
							cur_suf[j*pref_sz+i] = salsa[cur_suf[j*pref_sz+i]];
						salsa_faites[i + j * pref_sz] = nb_salsa;
						cur_suf[j * pref_sz + i] = (cur_suf[j*pref_sz+i] + 1)%suff_sz;
					}

			}
		}
	}
	for (int i(0); i < nb_tot; ++i)
	{
		int pref = i % pref_sz;
		if (salsa_faites[i] % 2 != nb_salsa%2)
			cur_suf[i] = salsa[cur_suf[i]];
		cout << pref_sz * cur_suf[i] + perm_pref[pref] << ' ';
	}
	cout << endl;

}

