    ans.at(i) = i;
    sal.at(i) = i;
  }

  for(int i=0;i<n3;i++){
    int a = sal.at(i);
    int j=0;
    while(a > 3){
      if(a%3 == 1){
        sal.at(i) += pow(3,j);
      }
      else if(a%3 == 2){
        sal.at(i) -= pow(3,j);
      }
      a = a/3;
      j++;
    }
    if(a == 1){
      sal.at(i) += pow(3,j);
    }
    else if(a == 2){
      sal.at(i)-= pow(3,j);
    }
    if(a == 3){
      sal.at(i) += pow(3,j+1);
    }
  }
  for(int i=0;i<(int)t.length();i++){
    if(t.at(i) == 'S'){
      salsa(ans,sal);
