	for (int i = 0; i < pow(3, n); i++)
	{
		ans[i] = i;
	}
	for (int i = 0; t[i]; i++)
	{
		if (t[i] == 'S')
		{
			one(n);
		}
		else if (t[i] == 'R')
		{
			two(n);
		}
	}
	for (int i = 0; i < pow(3, n); i++)
		cout << ans[i] << " ";
	cout << endl;
	return 0;
}

