#include <bits/stdc++.h>
using namespace std;

pair<vector<int>, vector<int>> solve(int n, const string& T) {
    if (n == 0) {
        vector<int> P = {0};
        vector<int> W(T.size(), 0);
        return {P, W}; 
    }
    //if(n==2) { return solve(1,T); }
    pair<vector<int>, vector<int>> Ans = solve(n-1,T);
    vector<int> P = Ans.first , L = Ans.second;
    int S = (int)P.size();
    vector<int> new_p(3*S),new_l((int)L.size()),dig(3*S);
    for(int i=0;i<3;++i) for(int j=0;j<S;++j) dig[j+S*i] = i;
    vector<int> last_change(3*S);
    int cnt = 0;  
    for(int i=0;i<(int)T.size();++i){
     	int wk = L[i];
        for(int j = wk ; j < 3*S ; j+=S){
			if(dig[j]!=0 && last_change[j]%2 != cnt%2) dig[j] = 3-dig[j];
            last_change[j] = cnt;
            if(dig[j] == 2) new_l[i] = j;
        }
        if(T[i] == 'R'){
            for(int j = wk ; j < 3*S ; j+=S) dig[j] = (dig[j]+1)%3;
        } 
        else{
          cnt++;
        }
    }  

    for(int i=0;i<3*S;++i){
      if(last_change[i]%2!=cnt%2 && dig[i]!=0) dig[i] = 3-dig[i];
      new_p[i] = P[i%S] + dig[i]*S;
    }
    return {new_p,new_l};
}

