        }
        a.at(j) = stoi(s);
        a.at(j)=binary1(j);
      }
    }
    }
    else{
      for(int j = 0; j < pow(3,n)-1; j++){
        a.at(j) += 1;
      }
      a.at(pow(3,n))=0;
    }
  }
  for(int j = 0; j < pow(3,n); j++){
    cout << a.at(j) << endl;
  }
}

