				preC = *it;
				continue;
			}

			if (*it == 'S' && it+1 != preS.end())
			{
				preC = 'S';
				continue;
			}

			S.push_back(*it);
			preC = *it;
		}
	}

	for (int i = 1; i < S.size(); i *= 2)
	{
		int j = 0;
		while (true)
		{
			if (j + i >= S.size()) break;
			string S1;
			string S2;

			for (int k = 0; k < i; ++k, ++j)
			{
				S1.push_back(S[j]);
			}

			for (int k = 0; k < i && j < S.size(); ++k, ++j)
			{
				S2.push_back(S[j]);
			}

			if (table.find(S1 + S2) == table.end())
			{
				unique_ptr<unordered_map<int, int>> D(new unordered_map<int,int>());
				rep(k, maxP)
				{
					(*D)[k] = (*(table[S2]))[(*(table[S1]))[k]];
				}

				table[S1 + S2] = std::move(D);
			}
		}
	}

	rep(i, maxP)
	{
		cout << (*(table[S]))[i];
		if (i+1 < maxP) cout << " ";
	}

	cout << endl;
}

