class hito{
public:
	bool sru;
	int moto;
	hito* go[3];
	hito(int in,int dep,int n){
		//cerr<<in<<" "<<dep<<" "<<n<<endl;
		moto=in;sru=false;
		if(dep<n){
			go[0]=new hito(in, dep+1,n);
			go[1]=new hito(in+san[dep],dep+1,n);
			go[2]=new hito(in+san[dep]*2,dep+1,n);
		}else{go[0]=nullptr;}
	}
	void dance(void){
		if(go[0]==nullptr){return;}
		if(sru){
			go[0]->sru=!go[0]->sru;
			go[1]->sru=!go[1]->sru;
			go[2]->sru=!go[2]->sru;
			swap(go[1],go[2]);
			sru=false;
		}
	}
	void runba(void){
		//cerr<<moto<<endl;
		if(go[0]==nullptr){return;}
		this->dance();
		auto aaa=go[2];
		go[2]=go[1];
		go[1]=go[0];
		go[0]=aaa;
		go[0]->runba();
	}
	void solve(int in,int dep){
		//cerr<<moto<<endl;
