int qpow(int a, int b)
{
	int res = 1;
	while (b)
	{
		if (b & 1)
		{
			res *= a;
		}
		a *= a;
		b /= 2;
	}
	return res;
}
void one(int n)
{
	for (int i = 0; i < qpow(3, n); i++)
	{
		int res = 0;
		int three = 1;
		while (ans[i])
		{
			int d = ans[i] % 3;
			if (d == 2)
			{
				res = res + three * 1;
			}
			else if (d == 1)
			{
				res = res + three * 2;
			}
			else if (d == 0)
			{
				res = res + three * 0;
			}
			three *= 3;
			ans[i] /= 3;
		}
		ans[i] = res;
	}
}
void two(int n)
{
	for (int i = 0; i < qpow(3, n); i++)
	{
		ans[i] += 1;
		ans[i] %= qpow(3, n);
	}
}
