int N;
struct Tree{
	int num;
	int ch[3];
	bool rev;
}T[2000010];int tot=1;
void S(int x)
{
	T[x].rev^=1;
	swap(T[x].ch[1],T[x].ch[2]);
	return ;
}
void pushdown(int x)
{
	if(!T[x].rev)return ;
	if(T[x].ch[0])S(T[x].ch[0]);
	if(T[x].ch[1])S(T[x].ch[1]);
	if(T[x].ch[2])S(T[x].ch[2]);
	T[x].rev=0;return ;
}
void R(int x)
{
	pushdown(x);
	int y=T[x].ch[2];
	T[x].ch[2]=T[x].ch[1];
	T[x].ch[1]=T[x].ch[0];
	T[x].ch[0]=y;
	if(y)R(y);
	return ;
}
void insert(int x)
{
	int now=x,o=1;
	for(int i=1;i<=N;i++)
	{
		int t=now%3;
		if(!T[o].ch[t])T[o].ch[t]=++tot;
		o=T[o].ch[t];
		now/=3;
	}
	T[o].num=x;
	return ;
}
int getval(int x,int y)
{
	pushdown(x);
	int t=y%3;
	if(!T[x].ch[t])return T[x].num;
	return getval(T[x].ch[t],y/3);
}
