cs int N=1000005,M=16;
int pw[M];
char str[N];
int n,m,tot,id[N],ans[N],lc[N],rc[N],pc[N],rev[N];
void build(int &u,int dep,int ps){
	u=++tot;
	if(dep==n){
		id[u]=ps;
		return;
	}
	build(lc[u],dep+1,ps);
	build(rc[u],dep+1,ps+pw[dep]);
	build(pc[u],dep+1,ps+2*pw[dep]);
}
void pushnow(int u){
	rev[u]^=1,swap(rc[u],pc[u]);
}
void pushdown(int u){
	if(!rev[u])return;
	if(lc[u])pushnow(lc[u]);
	if(rc[u])pushnow(rc[u]);
	if(pc[u])pushnow(pc[u]);
	rev[u]=0;
}
void move(int u,int dep){
	pushdown(u);
	if(dep==n)return;
	swap(rc[u],pc[u]),swap(rc[u],lc[u]);
	move(lc[u],dep+1);
}
void dfs(int u,int dep,int ps){
	pushdown(u);
	if(dep==n){ans[id[u]]=ps;return;}
	dfs(lc[u],dep+1,ps);
	dfs(rc[u],dep+1,ps+pw[dep]);
	dfs(pc[u],dep+1,ps+2*pw[dep]);
}
