void solve(istream &cin = std::cin, ostream &cout = std::cout)
{
	int n;

	cin >> n;

	string str;

	cin >> str;

	int num = (int) round(pow(3, n));

	vector<int> prs(num);

	for (int i = 0; i < prs.size(); i++)
	{
		int mult = 1;

		int to = i;

		while (mult < num)
		{
			if (to / mult % 3 != 0)
			{
				if (to / mult % 3 == 1)
					to += mult;
				else
					to -= mult;
			}

			mult *= 3;
		}

		prs[i] = to;
	}

	int shift = 0;
	bool rev = false;

	for (auto it : str)
	{
		assert(it == 'R' || it == 'S');

		if (it == 'R')
			shift += rev ? -1 : 1;
		else
			rev = !rev;
	}

	shift %= num;

	assert(num % 3 == 0);

	for (int i = 0; i < num; i++)
	{
		int ans = i + shift;

		ans %= num;

		if (ans < 0)
			ans += num;

		if (rev)
			ans = prs[ans];

		cout << ans << ' ';
	}

	cout << endl;
}


void stress(istream &cin = std::cin, ostream &cout = std::cout)
{
	int n;

	cin >> n;

	int num = (int) round(pow(3, n));

	vector<int> ord(num);
	iota(ord.begin(), ord.end(), 0);

	vector<int> prs(num);

	for (int i = 0; i < prs.size(); i++)
	{
		int mult = 1;

		int to = i;

		while (mult < num)
		{
			if (to / mult % 3 != 0)
			{
				if (to / mult % 3 == 1)
					to += mult;
				else
					to -= mult;
			}

			mult *= 3;
		}

		prs[i] = to;
	}

	string str;

	cin >> str;

	for (auto it : str)
	{
		assert(it == 'R' || it == 'S');

		if (it == 'R')
		{
			for (auto &i : ord)
				if (++i == num)
					i = 0;
		}
		else
			for (int i = 0; i < ord.size(); i++)
				ord[i] = prs[ord[i]];
	}

	for (auto it : ord)
		cout << it << ' ';

	cout << endl;
}


