// index 
bool isOK(vector<ll>& v, int index, int key, bool flag)
{
	if (flag) {

		if (v[index] >= key)
			return true;
		else
			return false;
	}
	else {
		if (v[index] > key)
			return true;
		else
			return false;
	}
}

// 
// flag == 1 >=, flag == 0 >
ll bs(vector<ll>& v, ll key, bool flag)
{
	int ng = -1;	//index = 0 -1
	int ok = SZ(v); // index = a.size()-1 a.size()

	/* ok  ng  */
	while (abs(ok - ng) > 1)
	{
		int mid = (ok + ng) / 2;

		if (isOK(v, mid, key, flag))
			ok = mid;
		else
			ng = mid;
	}
	return ok;
}

// 
ll gcd(ll a, ll b)
{
	if (a < b)
		swap(a, b);
	ll r = a % b;
	while (r != 0)
	{
		a = b;
		b = r;
		r = a % b;
	}
	return b;
}

