void apply()
{
	for (int i = 0; i < nn; i++)
	{
		bool found = false;
		for (auto &t : mp)
		{
			int cur = init[i];
			bool ok = true;
			for (int i = (int)t.from.length() -  1; i >= 0; i--)
			{
				ok &= (t.from[i] == ('0' + cur % 3));
				cur /= 3;
			}
			if (ok)
			{
				//cout << "found for " << i << ' ' << t.from << ' ' << t.to << ' ' << t.flip << endl;
				cur = init[i];
				int newv = 0;
				int pw = 1;
				for (int i = (int)t.from.length() -  1; i >= 0; i--)
				{
					newv = newv + (t.to[i] - '0') * pw;
					cur /= 3;
					pw *= 3;
				}
				for (int i = 0; i < n - (int)t.from.length(); i++)
				{
					int was = cur % 3;
					if (was != 0 && t.flip) was = 1 + 2 - was;
					newv = newv + was * pw;
					cur /= 3;
					pw *= 3;
				}
				init[i] = newv;
				found = true;
				break;
			}
		}
		assert(found);
	}
}

