        return res[left] + add * right;   
    }
    if (left) {    
        id = dp1[left]; 
        if (pref[id + 1] & 1) {
            right = xyz[right];             
        }
        ++right;
        left = 0; 
    }                     
    while (next[id] != -1) {        
        if ((pref[next[id]] - pref[id]) & 1) {
            right = xyz[right]; 
        }
        right++; 
        id = next[id];                                                                                                                                         
    }
    if ((pref[(int)s.size()] - pref[id]) & 1) {        
        right = xyz[right];     
    }
    left = to[id]; 
    return left + right * add; 
}

void solve() {
    for (int i = 0; i < N; ++i) {
        xyz[i] = get1(i, 'S') % N; 
    }
    cin >> n >> s; 
    int t = s.size(); 
    for (int i = 0; i < t; ++i) {
        pref[i + 1] = pref[i] + (s[i] == 'S'); 
    }

    for (int i = 0; i < N; ++i) dp1[i] = dp2[i] = -1; 
    next[t] = -1;
    for (int i = 0; i < N; ++i) kek1[i] = i;              
    for (int i = 0; i < N; ++i) res[i] = i; 
    for (int i = t - 1; i >= 0; --i) {
        for (int mask = 0; mask < N; ++mask) {
            int mask1; 
            if (s[i] == 'S') mask1 = xyz[mask]; 
            else mask1 = mask + 1; 
            if (mask1 >= N) mask1 -= N; 
            res1[mask] = res[mask1]; 
            kek2[mask] = kek1[mask1];
            if (mask && mask1 == 0) {
                dp2[mask] = i + 1; 
            } else {
                dp2[mask] = dp1[mask1]; 
            }                                                                                 
        }
        to[i] = kek2[0]; 
        next[i] = dp2[0]; 
        for (int mask = 0; mask < N; ++mask) {
            res[mask] = res1[mask]; 
            kek1[mask] = kek2[mask]; 
            dp1[mask] = dp2[mask]; 
            dp2[mask] = -1; 
        }
    }
    for (int i = 0; i < 7; ++i) add *= 3; 
    int tet = 1; 
    for (int i = 0; i < n; ++i) tet *= 3; 
    for (int ptr = 0; ptr < tet; ++ptr) {
        cout << get(ptr) % tet << " "; 
    }
//    cout << TIME; 
    return; 
}                                

