using namespace std;
const int MAX_N=5+1e6;
int pw[25];
struct RevOp{
	int d[MAX_N];
	bool flag;
	void init(int n){
		flag=0;
		for(int i=0;i<n;++i){
			for(int j=pw[i];j<pw[i]*2;++j)
				d[j]=1;
			for(int j=pw[i]*2;j<pw[i+1];++j)
				d[j]=2;
		}
	}
	int operator[](int x){ return flag?(d[x]==0?0:3-d[x]):d[x]; }
	void add(int x){ 
		if(!flag){
			d[x]++; 
			if(d[x]>=3) d[x]-=3; 
		}else{
			d[x]--;
			if(d[x]<0) d[x]+=3;
		}
	}
	void rev(){ flag^=1; }
}d;

inline void mo(int& x){x>=3?x-=3:0;}
int gbit(int x,int i){ return x/pw[i]%3; }
int gbit2(int x,int i){
	if(gbit(x,i)!=0) return d[x%pw[i+1]];
	int y=x%pw[i];
	return 3-d[y+pw[i]]-d[y+pw[i]*2];
}
void print(int n){
	for(int x=0;x<pw[n];++x){
		int ret=0;
		for(int i=0;i<n;++i)
			ret+=gbit2(x,i)*pw[i];
		printf("%d ",ret);
	}
	puts("");
}
