using namespace std;

void push(vector<int> &seq, bool command)
{
    if (command)
    {
        if (!seq.empty() && seq.back())
            seq.pop_back();
        else
            seq.push_back(command);
    }
    else
    {
        seq.push_back(command);
    }
}

void rec(int start, int jump, int size, vector<int> sequence, vector<int> &res)
{
    if (jump == res.size())
    {
        return;
    }

    vector<vector<int>> new_seq(3);
    vector<int> digit = {0, 1, 2};

    for (auto command : sequence)
    {
        if (command)
        {
            for (int i = 0; i < 3; ++i)
            {
                push(new_seq[i], command);
                if (digit[i])
                    digit[i] = 3 - digit[i];
            }
        }
        else
        {
            for (int i = 0; i < 3; ++i)
            {
                digit[i]++;

                if (digit[i] == 3)
                {
                    digit[i] = 0;
                    push(new_seq[i], command);
                }
            }
        }
    }

    int finish = start + size * jump;
    int ix = 0;

    for (int i = start; i < finish; i += jump)
    {
        res[i] += digit[ix] * jump;
        if (++ix == 3)
            ix = 0;
    }

    for (int i = 0; i < 3; ++i)
    {
        rec(start + jump * i, jump * 3, size / 3, new_seq[i], res);
    }
}

int main()
{
    ios_base::sync_with_stdio(0);
